<?php

/**
 * AnnexebudgetligneTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class AnnexebudgetligneTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object AnnexebudgetligneTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Annexebudgetligne');
    }

    public function getByAnnexe($id, $ordre = 0) {
        $q = $this->createQuery('a')
                ->where('a.id_annexebudget = ?', $id);
        if ($ordre == 0)
            $q = $q->orderBy('LENGTH(a.rang), a.rang');
        else
            $q = $q->orderBy('LENGTH(a.rang),a.rang desc');

        $q = $q->execute();
        return $q;
    }

}
