<?php

/**
 * ApplicationmoduleTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ApplicationmoduleTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ApplicationmoduleTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Applicationmodule');
    }
    public function getByApplicationAndModule($libelle_app,$libelle_module){
        $q = $this->createQuery('pm')
        ->leftJoin('pm.Application am')
        ->where('pm.libelle = ?', $libelle_module)
        ->andWhere('am.libelle = ?' , $libelle_app);
        
            return $q->execute()->getFirst();
    }
}