        if ($this->getAgents()->getSexe()->getId() == 1):
            $html.="M.";
        else:
            $html.="Mme.";
        endif;

        $html.=$this->getAgents()->getNomcomplet() . "    " . $this->getAgents()->getPrenom() . ", "
