class Besoinsdeformation extends BaseBesoinsdeformation {

    public function __toString() {

        return "" . $this->getBesoins();
    }

    public function getDate() {
        echo date('Y');
    }

     public function ReadHtmllisteBesoinsFormation() {

        $html = ' <h3>Liste des Besoins de Formation </h3>'
        ;
        $html.=
                '<table border="1" style="padding:1%">
           
            <tbody>
                <tr>
                   <th style="width: 20%;"><b><label>Numéro</label></b></th>
                   <th style="width: 60%;"><b><label>Besoins de Formation </label></b></th>
                    <th style="width: 20% text-align:left;"><b><label>Année</label></b></th>
              
                 
                </tr>
                ';

        $for = Doctrine_Core::getTable('besoinsdeformation')->findAll();
        $i = 1;
        foreach ($for as $p):
            $html.= '<tr>'
                    . '<td>' . $i . '</td>'
                    . '<td style="text-align:left;">' . $p->getBesoins() . '</td>'
                    . '<td style="text-align:left;">' . $p->getAnnee(). '</td>'
                    . '</tr>';
            $i++;
        endforeach;
        $html.='<tbody></table>';

        return $html;
    }

}
