    public function findBySousCorpsOrderByLibelle($id_sous_corps) {
        $query = $this->createQuery('a');
        $query->from('Categorierh a')
                ->where('a.id_souscorps = ' . $id_sous_corps)
                ->orderBy('a.libelle');
        return $query->execute();
    }
    
    public function findByCorpsOrderByLibelle($id_corps) {
        $query = $this->createQuery('a');
        $query->from('Categorierh a')
                ->where('a.id_corps = ' . $id_corps)
                ->orderBy('a.libelle');
        return $query->execute();
    }

