<?php

/**
 * CertificatretenueTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class CertificatretenueTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object CertificatretenueTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Certificatretenue');
    }

    public function getByAnnee($annee = '') {
        $q = $this->createQuery('d')
                ->select('d.*')
                ->from('Certificatretenue d')
                ->leftJoin('d.Documentbudget db');
        if ($annee != '')
            $q = $q->andWhere("EXTRACT(YEAR FROM db.datecreation) = '" . $annee . "'");

        $q = $q->orderBy('d.datecreation');
        return $q->execute();
    }

    public function getByBudget($id_budget = '') {
        $q = $this->createQuery('d')
                ->select('d.*')
                ->from('Certificatretenue d')
                ->leftJoin('d.Documentbudget db');
        if ($id_budget != '')
            $q = $q->andWhere("d.id_documentbudget = '" . $id_budget . "'");

        $q = $q->orderBy('d.datecreation');
        return $q->execute();
    }

}
