<?php

/**
 * DirectionTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class DirectionTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object DirectionTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Direction');
    }

    public function getAllOrderByLibelle() {
        $query = $this->createQuery('di');
        $query->select('di.id as id, di.libelle as libelle, d.id as id_demandeur')
                ->from('Direction di')
                ->leftJoin('di.Demandeur d')
                ->orderBy('di.libelle');
        return $query->execute();
    }

}
