class Domaineuntilisation extends BaseDomaineuntilisation {

    public function __toString() {
        return "" .$this->getCode().'.' . $this->getLibelle();
    }
  public function ReadHtmllisteDomaineutilisation() {

        $html = ' <h3>Liste des Domaines d\'utilisation </h3>'
        ;
        $html.=
                '<table border="1" style="padding:1%">
           
            <tbody>
                <tr>
                   <th style="width: 15%;"><b><label>Numéro</label></b></th>
                   <th style="width: 10% text-align:left;"><b><label>Code</label></b></th>
                   <th style="width: 75% text-align:left;"><b><label>Libellé</label></b></th>
              
                 
                </tr>
                ';

        $for = Doctrine_Core::getTable('domaineuntilisation')->findAll();
        $i = 1;
        foreach ($for as $p):
            $html.= '<tr>'
                    . '<td>' . $i . '</td>'
                    . '<td style="text-align:left;">' . $p->getCode() . '</td>'
                    . '<td style="text-align:left;">' . $p->getLibelle() . '</td>'
                    . '</tr>';
            $i++;
        endforeach;
        $html.='<tbody></table>';

        return $html;
    }

}
