class Emplacement extends BaseEmplacement {

    public function __toString() {
        if ($this->getIdBureau() != null) {
            $br_new = new Bureaux();
            $bureau = Doctrine_Core::getTable('bureaux')->findOneById($this->getIdBureau());
            if ($bureau) {
                $br_new = $bureau;
//            $site = $br_new->getEtage()->getSite();
                $adresse = $br_new->getEtage()->getSite()->getAdresse();
                return "" . $adresse . " " . $br_new;
            } else {
                return "";
            }
        } else {
            return "";
        }
    }

}
