<?php

/**
 * FamexpdesTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FamexpdesTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FamexpdesTable
     */
    public static function getInstance(){
        return Doctrine_Core::getTable('Famexpdes');
    }
    
    public function getForTransfert() {
        $q = $this->createQuery('f')
                ->where("f.famille like '%SECRETARIAT%'")
                ->orWhere("trim(f.famille) = trim('B.O CENTRALE')")
                ->orderBy('f.famille')
                ->execute();
        return $q;
    }
}