<?php

/**
 * FormejuridiqueTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FormejuridiqueTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object FormejuridiqueTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Formejuridique');
    }

    public function getAllFormeJuridique($libelle = '') {
        $q = $this->createQuery('fj');
        if ($libelle != '') {
            $q = $q->andWhere('UPPER(fj.libelle) like ?', '%' . $libelle . '%');
        }

        return $q;
    }

    public function getForExiste($libelle, $id) {
        $q = $this->createQuery('fj')
                ->where('fj.id <> ' . $id)
                ->andWhere('fj.libelle = ?', $libelle);

        return $q->execute();
    }

    public function getAllOrder() {
        $q = $this->createQuery('fj')
                ->orderBy('fj.libelle');

        return $q->execute();
    }

}
