<?php

/**
 * GouverneraTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class GouverneraTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object GouverneraTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Gouvernera');
    }
    
    public function LoadVilleByIdPays($id) {
        $q = $this->createQuery('g')
                ->where('g.id_pays = ?', $id)
                ->orderBy('g.gouvernera');

        return $q->execute();
    }
   
}