<?php

/**
 * LignefraisgenerauxTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class LignefraisgenerauxTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object LignefraisgenerauxTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Lignefraisgeneraux');
    }

    public function getByRapportAndType($rapport_id, $numero) {
        $q = $this->createQuery('l')
                ->leftJoin('l.Plandossiercomptable pc')
                ->andWhere('l.id_fraisgeneraux = ?', $rapport_id)
                ->andWhere("trim(pc.numerocompte) LIKE '" . trim($numero) . "%'");

        $q->orderBy('trim(pc.numerocompte)');

        return $q->execute();
    }

}
