class Ligneinventaire extends BaseLigneinventaire
{
    public function MisajourLigne($idinve,$idart,$qtetheorique){
        $lgIn=new Ligneinventaire();
        $ligne=Doctrine_Core::getTable('ligneinventaire')->findOneByIdInventaireAndIdArticle($idinve,$idart);
        if($ligne)
            $lgIn=$ligne;
        else{
            $lgIn->setIdInventaire($idinve);
            $lgIn->setIdArticle($idart);
            $lgIn->setQtetheorique($qtetheorique);
            $lgIn->save();
        }
        return $lgIn;
    }
}