<?php

/**
 * MarchesTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class MarchesTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object MarchesTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Marches');
    }

    public function getAllOrderByNumero() {
        $q = $this->createQuery('Marches')
                ->orderBy('numero')
                ->execute();
        return $q;
    }

    public function getAllMarcheEnCours() {
        $q = $this->createQuery('m')
                ->from('Marches m')
                ->where('m.datecommencement IS NULL')
                ->orWhere("(m.datecommencement IS NOT NULL AND (m.datecommencement + m.delai >= localtimestamp at time zone 'UTC'))")
                ->orderBy('m.datecreation desc');

        return $q;
    }

}
