<?php

/**
 * NaturebanqueTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class NaturebanqueTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object NaturebanqueTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Naturebanque');
    }

    public function getAllByLibelle($libelle = '') {
        $q = $this->createQuery('a');
        if ($libelle != '') {
            $q = $q->andWhere('UPPER(a.libelle) like ?', '%' . strtoupper($libelle) . '%');
        }

        return $q->execute();
    }

}
