     * @return object NaturetravauxTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Naturetravaux');
    }

    public function getAllNaturetravaux($libelle = '') {
        $q = $this->createQuery('n');
        if ($libelle != '') {
            $q = $q->andWhere('UPPER(n.libelle) like ?', '%' . $libelle . '%');
