<?php

/**
 * PapierordrepostalTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class PapierordrepostalTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object PapierordrepostalTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Papierordrepostal');
    }

    public function getByListeRefpapier($references) {
        $q = $this->createQuery('pc')
                ->whereIn('pc.repapier', (array) $references)
                ->execute();
        return $q;
    }
    
    public function getFirstOrderVierge($id_banque){
        $q = $this->createQuery('pc')
                ->leftJoin('pc.Carnetordrepostal cop')
                ->where('pc.etat = ?', false)
                ->andWhere('pc.annule = ?', false)
                ->andWhere('cop.id_compte = ?', $id_banque);
        return $q->execute()->getFirst();
    }

}
