<?php

/**
 * ProfilmoduleTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ProfilmoduleTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object ProfilmoduleTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Profilmodule');
    }

    public function getByIdApplicationAndModule($id_application, $libelle) {
       
        $q = $this->createQuery('pm')
                        ->leftJoin('pm.Applicationmodule am')
                        ->where('am.libelle = ?', $libelle)
                        ->andWhere('pm.id_profilapplication = ' . $id_application);
                        
        return $q->execute()->getFirst();
    }

}
