class Projet extends BaseProjet
{
    public function __toString() {
        return "".$this->getLibelle();
    }
    
        public function ReadHtmlAlllisteProjets() {

        $html = ' <h3>Liste des Projets </h3>'
        ;
        $html.=
                '<table border="1" style="padding:1%">
                <tbody>
           
           
                <tr>
                  <th style="width: 20%;"><b><label>Numéro</label></b></th>
                    <th style="width: 80% text-align:left;"><b><label>Libellé</label></b></th>
                 
                </tr>
                ';
        
        $projet = Doctrine_Core::getTable('projet')->findAll();

        $i = 1;
        foreach ($projet as $p):
            $html.= '<tr>'
                . '<td>' . $i . '</td>'
                . '<td style="text-align:left;">' . $p->getLibelle() . '</td>'
                . '</tr>';
        $i++;
        endforeach;
        $html.='<tbody></table>';

        return $html;
    }
}