<?php

/**
 * RapporttravauxTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class RapporttravauxTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object RapporttravauxTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Rapporttravaux');
    }

    public function getTotauxByRapport($id) {
        $q = Doctrine_Core::getTable('Rapporttravaux')
                        ->createQuery('r')
                        ->select('r.id, COALESCE(SUM(a.mre), 0) as tmre, COALESCE(SUM(a.dps), 0) as tdps, COALESCE(SUM(a.maint), 0) as tmaint, COALESCE(SUM(a.bat), 0) as tbat, COALESCE(SUM(a.plant), 0) as tplant')
                        ->leftJoin('r.Articlerapporttravaux a')
                        ->where('r.id = ?', $id)
                        ->groupBy('r.id')
                        ->execute()->getLast();

        return $q;
    }

}
