class Rubrique extends BaseRubrique {

    public function __toString() {
       
            return '' . $this->getLibelle();
        
    }

    public function getRubriquebysousrubrique() {
        if (!$this->getIdRubrique())
            return null;
        $rubrique = Doctrine_Core::getTable('rubrique')->findOneByIdRubrique($this->getIdRubrique());
        return $rubrique;
    }

    public function getSousrubrique() {

        if ($this->getIdRubrique()) {
            $rubriques = Doctrine_Core::getTable('rubrique')->findById($this->getIdRubrique());
           
            return $rubriques;
        }
        return null;
    }
    public function getRubriqueparent() {

        
        if ($this->getIdRubrique())
            $rubrique = RubriqueTable::getInstance()->findOneById($this->getIdRubrique());
        else
            $rubrique = $this;
             
        return $rubrique;
    }

    public function getSousRubriqueBudgetaire() {
      
        if ($this->getIdRubrique()){
            $rubrique = RubriqueTable::getInstance()->findOneById($this->getId());
            return $rubrique;
        }
        return null;
           
    }
}
