<?php

/**
 * SecteuractiviteTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class SecteuractiviteTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object SecteuractiviteTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Secteuractivite');
    }

    public function getAllSecteurActvite($libelle = '') {
        $q = $this->createQuery('sa');
        if ($libelle != '') {
            $q = $q->andWhere('UPPER(sa.libelle) like ?', '%' . $libelle . '%');
        }

        return $q;
    }

    public function getForExiste($libelle, $id) {
        $q = $this->createQuery('sa')
                ->where('sa.id <> ' . $id)
                ->andWhere('sa.libelle = ?', $libelle);

        return $q->execute();
    }

    public function getAllByOrder() {
        $q = $this->createQuery('A')
                ->orderBy('A.libelle');

        return $q->execute();
    }

}
