class Servicerh extends BaseServicerh {

    public function __toString() {
        return $this->getLibelle() . " ";
    }

    public function ReadHtmlAlllisteServices($idd) {

        $html = ' <h3>Liste des Services </h3>'
        ;
        $html.=
                '<table border="1" style="padding:1%">
           
            <tbody>
                <tr>
                     <td style="width: 20%;"><b><label>Numéro</label></b></td>
                    <td style="width: 80% text-align:left;"><b><label>Libellé</label></b></td>
                 
                 
                </tr>
                ';

        $sousd = Doctrine_Core::getTable('servicerh')->findAll();

        $i = 1;
        foreach ($sousd as $p):
            $html.= '<tr>'
                    . '<td>' . $i . '</td>'
                    . '<td style="text-align:left;">' . $p->getLibelle() . '</td>'
                    . '</tr>';
            $i++;
        endforeach;
        $html.='<tbody></table>';

        return $html;
    }

//service par sous direction
    public function ReadHtmlAlllisteServicespardirection($idd) {
  $direction = Doctrine_Core::getTable('direction')->findOneById($idd); 
 
