<?php

/**
 * ServicerhTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ServicerhTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object ServicerhTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Servicerh');
    }

    public function getAllOrderByLibelle() {
        $query = $this->createQuery('a');
        $query->select('s.id as id, s.libelle as libelle, d.id as id_demandeur')
                ->from('Servicerh s')
                ->leftJoin('s.Demandeur d')
                ->orderBy('s.libelle');
        return $query->execute();
    }

    public function getServiceDirection($idd) {
        $query = $this->createQuery('a');
        $query->select('s.id, s.libelle')
                ->from('Servicerh s')
                ->leftJoin('s.Sousdirection d')
                ->Where('d.id_direction=?', $idd)
                ->orderBy('s.libelle');
        return $query->execute();
    }

    public function getByListeLibelle($libelles) {
        $q = $this->createQuery('d')
                ->whereIn('upper(trim(d.libelle))', (array) $libelles)
                ->execute();
        return $q;
    }

}
