<?php

/**
 * TableauxammortisementTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TableauxammortisementTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object TableauxammortisementTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Tableauxammortisement');
    }

    public function getForPrintList($id, $id_categorie, $id_famille, $id_sousfamille, $annee) {
        $q = $this->createQuery('t')
                ->from('Tableauxammortisement t')
                ->leftJoin('t.Immobilisation i');
        if ($id != '')
            $q = $q->where('i.id = ' . $id);

        if ($id_categorie != '')
            $q = $q->andWhere('i.id_categorie = ' . $id_categorie);

        if ($id_famille != '')
            $q = $q->andWhere('i.id_famille = ' . $id_famille);

        if ($id_sousfamille != '')
            $q = $q->andWhere('i.id_sousfamille = ' . $id_sousfamille);

        if ($annee != '') {
            $q = $q->andWhere("t.datetableux >= '" . $annee . '-01-01' . "'");
            $q = $q->andWhere("t.datetableux <= '" . $annee . '-12-31' . "'");
        }

        $q = $q->orderBy('i.designation');

        return $q->execute();
    }

}
