<?php

/**
 * TransfertbudgetTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TransfertbudgetTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object TransfertbudgetTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('Transfertbudget');
    }

    public function getByAnnee($annee = '') {
        $q = $this->createQuery('t')
                ->select('t.*')
                ->from('Transfertbudget t');
        if ($annee != '')
            $q = $q->andWhere("EXTRACT(YEAR FROM t.datecreation) = '" . $annee . "'");

        $q = $q->orderBy('t.datecreation');
        return $q->execute();
    }

}
