<?php

/**
 * TypejournalTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TypejournalTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TypejournalTable
     */
    public static function getInstance(){
        return Doctrine_Core::getTable('Typejournal');
    }
    
    public function getAllTypeJournal($libelle = '') {
        $q = $this->createQuery('tj');
        if ($libelle != '') {
            $q = $q->andWhere('UPPER(tj.libelle) like ?', '%' . $libelle . '%');
        }

        return $q;
    }

    public function getForExiste($libelle, $id) {
        $q = $this->createQuery('tj')
                ->where('tj.id <> ' . $id)
                ->andWhere('tj.libelle = ?', $libelle);

        return $q->execute();
    }
}